import React from "react";
import ReactDOM from "react-dom/client";
import { useState } from "react";

const App = () => {
 const [contador, setContador] = useState(0);

 setInterval(() => {
  contador++;
 }, 2000);

 return (
  <div>
   <h1>{contador}</h1>
  </div>
 );
};

const root = ReactDOM.createRoot(document.getElementById("root"));
root.render(<App />);
