import React from "react";
import ReactDOM from "react-dom/client";
import { useState } from "react";

const App = () => {
 const [contador, setContador] = useState(25);

 console.log("Se está renderizando de nuevo");

 const handleClick = (sumar, restar) => {
  sumar = setContador(contador + 1);
  restar = setContador(contador - 1);
 };

 const isEven = contador % 2 === 0;

 return (
  <div>
   <p>El valor del contador es: </p>
   <h1>{contador}</h1>
   <button onClick={handleClick}>Incrementar</button>
   <button onClick={handleClick}>Decrementar</button>
   <p>{isEven && "es par"}</p>
  </div>
 );
};

const root = ReactDOM.createRoot(document.getElementById("root"));
root.render(<App />);
