import React from "react";
import ReactDOM from "react-dom/client";
import { useState } from "react";

const App = () => {
 const [contador, setContador] = useState(25);

 console.log("Se está renderizando de nuevo");

 const handleClick = (operacion) => {
  operacion === "sumar" ? setContador(contador + 1) : setContador(contador - 1);
 };

 const isEven = contador % 2 === 0;

 return (
  <div>
   <p>El valor del contador es: </p>
   <h1>{contador}</h1>
   <button onClick={handleClick("sumar")}>Incrementar</button>
   <button onClick={() => handleClick("restar")}>Decrementar</button>
   <p>{isEven ? "ES PAR" : "ES IMPAR"}</p>
  </div>
 );
};

const root = ReactDOM.createRoot(document.getElementById("root"));
root.render(<App />);
