import React from "react";
import ReactDOM from "react-dom/client";
import "./styles.css";
import { useState } from "react";

const App = () => {
 const [counter, setCounter] = useState({
  left: 0,
  right: 0,
  mensaje: "Practicando con React",
  clicks: 0,
 });

 const HandleLeft = () => {
  const nuevoEstado = {
   ...counter,
   left: counter.left + 1,
   clicks: counter.clicks + 1,
  };

  setCounter(nuevoEstado);
 };

 const HandleRight = () => {
  const nuevoEstado = {
   ...counter,
   right: counter.right + 1,
   clicks: counter.clicks + 1,
  };
  setCounter(nuevoEstado);
 };

 const arreglos = ([1, 2, 3], [4, 5, 6]);

 const [arr1, arr2] = arreglos;

 console.log(arr1);
 console.log(arr2);

 return (
  <div className="container">
   {counter.left}
   <button onClick={HandleLeft}>Left</button>
   <button onClick={HandleRight}>Right</button>
   {counter.right}
   <p>{counter.mensaje}</p>
   <p>NÃºmero de clicks: {counter.clicks}</p>
  </div>
 );
};

const root = ReactDOM.createRoot(document.getElementById("root"));
root.render(<App />);
