import React from "react";
import ReactDOM from "react-dom/client";
import "./styles.css";
import { useState } from "react";

const App = () => {
 const [counter, setCounter] = useState({
  left: 0,
  right: 0,
  mensaje: "Practicando con React",
  clicks: 0,
 });

 const [clicks, setClicks] = useState([]);

 const HandleLeft = () => {
  const nuevoEstado = {
   ...counter,
   left: counter.left + 1,
   clicks: counter.clicks + 1,
  };

  setCounter(nuevoEstado);
  setClicks((prevValue) => [...prevValue, "L"]);
 };

 const HandleRight = () => {
  setCounter({
   ...counter,
   right: counter.right + 1,
   clicks: counter.clicks + 1,
  });
  setClicks((prevValue) => prevValue.concat("R"));
 };

 return (
  <div className="container">
   {counter.left}
   <button onClick={HandleLeft}>Left</button>
   <button onClick={HandleRight}>Right</button>
   {counter.right}
   <p>Has pulsado {counter.clicks} veces</p>
   <p>{counter.mensaje}</p>
   <p>{clicks.join(",")}</p>
  </div>
 );
};

const root = ReactDOM.createRoot(document.getElementById("root"));
root.render(<App />);
